import { STAFFS, DEPARTMENTS } from "../shared/Staff";
import { baseUrl } from "../shared/baseUrl"
export var initialState = {
  staffs: [],
  department:[],
};
// const initialState=[]
let headersList = {
  "Accept": "*/*",
  "User-Agent": "Thunder Client (https://www.thunderclient.com)"
 }
 fetch(baseUrl, { 
   method: "GET",
   headers: headersList
 }).then(function(response) {
   return response.text();
 }).then(function(data) {
   initialState.staffs=[
    ...initialState.staffs, 
    ...JSON.parse(data)
   ];
   console.log(initialState.staffs);
  })

const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD":
      return {
        ...state,
        staffs: [
          ...state.staffs,
          action.payload
        ],
      };

    default:
      return state;
  }
};
export default rootReducer;
