  }
 console.log(initialState);
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD":
      return {
        ...state,
        staffs: [
          ...state.staffs,
          action.payload
        ],
      };

    default:
      return state;
  }
};
export default rootReducer;
