import { STAFFS, DEPARTMENTS } from "../shared/Staff";
import { useEffect } from "react";
import { FetchApi } from "./actionCreate";
export const initialState = {
  staffs: [],
  department:[],
};
fe
const rootReducer = (state = initialState, action) => {
  switch (action.type) {
    case "ADD":
      return {
        ...state,
        staffs: [
          ...state.staffs,
          action.payload
        ],
      };

    default:
      return state;
  }
};
export default rootReducer;
