import React, { Component } from 'react'
import {Card,CardImg,CardBody,CardTitle} from "reactstrap"

export class DishDetailComponents extends Component {
    constructor(props) {
        super(props)
        this.state = {
            selectedDish:null
        }
    }
  render() {
    
    const menu=this.props.dish.map((dish)=> {
        return(
            <Card key={dish.id}>
              <div class="col-12 col-md-5">
                    <CardImg  src={dish.image} alt={dish.name}>
                    </CardImg>
                    <CardBody>
                            <CardTitle>{dish.name}</CardTitle>
                            <CardTitle>{dish.description}</CardTitle>
                    </CardBody>
              </div>
            </Card>
        )
    })
    return (
        <>
        <div>

      <div className="row">
            {menu}
      </div>
      <div className="row">
            {/* {this.renderDish(this.state.selectedDish)} */}
        </div>
        </div>
      </>
    )
  }
}

export default DishDetailComponents
